export default {
	Item,
	integerValuesKnapsack,
	integerValuesKnapsackUnbounded,
	integerWeightsKnapsack,
	integerWeightsKnapsackUnbounded,
	knapsackApprox,
	knapsackGreedy,
	knapsackUnboundedGreedy,
	orderedByDecreasingUtility,
};

export {default as Item} from './Item.js';
export {default as integerValuesKnapsackUnbounded} from './integerValuesKnapsackUnbounded.js';
export {default as integerValuesKnapsack} from './integerValuesKnapsack.js';
export {default as integerWeightsKnapsackUnbounded} from './integerWeightsKnapsackUnbounded.js';
export {default as integerWeightsKnapsack} from './integerWeightsKnapsack.js';
export {default as knapsackGreedy} from './knapsackGreedy.js';
export {default as knapsackApprox} from './knapsackApprox.js';
export {default as orderedByDecreasingUtility} from './orderedByDecreasingUtility.js';
export {default as knapsackUnboundedGreedy} from './knapsackUnboundedGreedy.js';

import assert from 'assert';

import {sum} from '@iterable-iterator/reduce';

/**
 * Exact DP solution to the 0-1 knapsack problem with integer values given a known
 * upper bound V on OPT. Runs in O(nV) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Number} V Any upper bound on OPT >= 0.
 * @param {Array} m Memory buffer.
 * @return {Number} Objective value of the optimum.
 */
const integerValuesKnapsack = (
	v,
	w,
	n,
	W,
	V = sum(v),
	m = new w.constructor(V + 1).fill(W + 1),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(V) && V >= 0);
	assert(m.length >= V + 1);
	m[V] = 0;
	for (let i = 0; i < n; ++i) {
		const wi = w[i];
		const vi = v[i];
		assert(Number.isInteger(vi) && vi >= 0);
		const k = V - vi + 1;
		for (let j = 0; j < k; ++j) {
			m[j] = Math.min(m[j], m[j + vi] + wi);
		}
	}

	for (let j = 0; j < V; ++j) {
		if (m[j] <= W) return V - j;
	}

	return 0;
};

export default integerValuesKnapsack;

import assert from 'assert';
import {increasing} from '@total-order/primitive';
import {max} from '@iterable-iterator/reduce';
import {map} from '@iterable-iterator/map';
import {range} from '@iterable-iterator/range';

/**
 * Exact DP solution to the unbounded knapsack problem with integer values
 * given a known upper bound V on OPT. Runs in O(nV) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Number|BigInt} zero The number 0.
 * @param {Number} V Any upper bound on OPT >= 0.
 * @param {Array} m Memory buffer.
 * @return {Number} Objective value of the optimum.
 */
const integerValuesKnapsackUnbounded = (
	v,
	w,
	n,
	W,
	zero = 0,
	V = Math.floor(
		W *
			max(
				increasing,
				map((i) => v[i] / w[i], range(n)),
				zero,
			),
	),
	m = new w.constructor(V + 1).fill(W + 1),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(V) && V >= 0);
	assert(m.length >= V + 1);
	m[0] = 0;
	for (let j = 1; j <= V; ++j) {
		let temporary = m[j];
		for (let i = 0; i < n; ++i) {
			const wi = w[i];
			const vi = v[i];
			assert(Number.isInteger(vi) && vi >= 0);
			const k = j - vi;
			// TODO sort by value to avoid branching
			// from larger to smaller so that m is scanned
			// from left to right
			if (k >= 0) temporary = Math.min(temporary, m[k] + wi);
		}

		m[j] = temporary;
	}

	for (let j = V; j > 0; --j) {
		if (m[j] <= W) return j;
	}

	return 0;
};

export default integerValuesKnapsackUnbounded;

import assert from 'assert';

/**
 * Exact DP solution to the 0-1 knapsack problem with integer weights.
 * Runs in O(nW) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Array} m Memory buffer.
 * @return {Number} Objective value of the optimum.
 */
const integerWeightsKnapsack = (
	v,
	w,
	n,
	W,
	m = new v.constructor(W + 1).fill(0),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(W) && W >= 0);
	assert(m.length >= W + 1);
	for (let i = 0; i < n; ++i) {
		const wi = w[i];
		const vi = v[i];
		assert(Number.isInteger(wi) && wi >= 0 && wi <= W);
		const k = W - wi + 1;
		for (let j = 0; j < k; ++j) {
			m[j] = Math.max(m[j], m[j + wi] + vi);
		}
	}

	return m[0];
};

export default integerWeightsKnapsack;

import assert from 'assert';

/**
 * Exact DP solution to the unbounded knapsack problem with integer weights.
 * Runs in O(nW) time.
 *
 * @param {Array} v Values.
 * @param {Array} w Weights.
 * @param {Number} n Size of the problem.
 * @param {Number} W Size of the knapsack.
 * @param {Array} m Memory buffer.
 * @return {Number} Objective value of the optimum.
 */
const integerWeightsKnapsackUnbounded = (
	v,
	w,
	n,
	W,
	m = new v.constructor(W + 1).fill(0),
) => {
	assert(v.length === n);
	assert(w.length === n);
	assert(Number.isInteger(W) && W >= 0);
	assert(m.length >= W + 1);
	for (let j = 1; j <= W; ++j) {
		let temporary = m[j];
		for (let i = 0; i < n; ++i) {
			const wi = w[i];
			const vi = v[i];
			assert(Number.isInteger(wi) && wi >= 0 && wi <= W);
			const k = j - wi;
			// TODO sort by weight to avoid branching
			// from larger to smaller so that m is scanned
			// from left to right
			if (k >= 0) temporary = Math.max(temporary, m[k] + vi);
		}

		m[j] = temporary;
	}

	return m[W];
};

export default integerWeightsKnapsackUnbounded;

